	IntPtr parrayPtr = IntPtr.Zero;
	IntPtr pcmapPtr = IntPtr.Zero;

	int _Result = Natives.pixGetMostPopulatedColors(pixs.Pointer,   sigbits,   factor,   ncolors, out  parrayPtr, out pcmapPtr);
	
