	IntPtr boxPtr = IntPtr.Zero; 	if (box != null) {boxPtr = box.Pointer;}

	int _Result = Natives.pixColorGray(pixs.Pointer, boxPtr,   type,   thresh,   rval,   gval,   bval);
	
