	IntPtr pnamaxPtr = IntPtr.Zero;

	int _Result = Natives.pixMinMaxNearLine(pixs.Pointer,   x1,   y1,   x2,   y2,   dist,   direction, out pnaminPtr, out pnamaxPtr, out  pminave, out  pmaxave);
	
